import { BreakPoint, BreakPoints } from '../types';
/**
 * 是否为字符串
 * @param str
 */
export declare function isString(str: any): str is string;
export declare function isNumber(num: any): num is number;
/**
 * 获取当前屏幕断点
 * @param breakPoints
 */
export declare function getCurBreakPoint(breakPoints: BreakPoints): BreakPoint;
/**
 * 获取最大断点列数
 */
export declare function getMaxNumberOfColumns(breakPoints?: BreakPoints): number;
/**
 * 为数值包裹自动单位
 * @param value
 * @param unit
 */
export declare function wrapUnit(value: string | number, unit?: string): string;
/**
 * 是否为一个有效的 gap 值:
 * eg: 0.9 / -9px / auto
 * @param val
 */
export declare function isValidGap(val: any): boolean;
/**
 * 获取最终的 gap 值
 * @param contextGap 上下文中的 gap 值
 * @param propGap 属性中定义的 gap 值
 */
export declare function getGapVal(contextGap: any, propGap: any): string;
/**
 * 简单节流函数
 * @param fn
 * @param delay
 */
export declare function throttle(fn: any, delay?: number): (...args: any[]) => void;
